import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { auth, signInWithEmailAndPassword, createUserWithEmailAndPassword, GoogleAuthProvider, signInWithPopup } from "./firebase";

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  async function emailLogin() {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      const token = await auth.currentUser.getIdToken(true);
      if (token) {
        localStorage.setItem("@token", token);
        navigate("/book-list");
      }
    } catch (error) {
      console.log(error);
    }
  }

  async function emailRegister() {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      const token = await auth.currentUser.getIdToken(true);
      if (token) {
        localStorage.setItem("@token", token);
        navigate("/book-list");
      }
    } catch (error) {
      console.log(error);
    }
  }

  async function googleLogin() {
    const provider = new GoogleAuthProvider();
    try {
      const result = await signInWithPopup(auth, provider);
      const token = await auth.currentUser.getIdToken(true);
      if (token) {
        localStorage.setItem("@token", token);
        navigate("/book-list");
      }
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div>
      <form>
        <h3>Login Here</h3>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          placeholder="Email or Phone"
          id="username"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          placeholder="Password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={emailLogin} className="login-button">
          LOGIN
        </button>
        <button onClick={emailRegister} className="register-button">
          REGISTER
        </button>
        <button onClick={googleLogin} className="login-button">
          LOGIN WITH GOOGLE
        </button>
      </form>
    </div>
  );
}
